import React, { useState, useEffect } from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css"
//Explore more Monday React Components here: https://style.monday.com/
import AttentionBox from "monday-ui-react-core/dist/AttentionBox.js"
import Button from "monday-ui-react-core/dist/Button.js"

const monday = mondaySdk();


const App = () => {

  const [score, setScore] = useState(10)
  const [rating, setRating] = useState("AAA")
  const [color, setColor] = useState("black")

  const [mainBoardID, setMainBoardID] = useState(null)
  const [mainBoardItems, setMainBoardItems] = useState(null)

  function fetchBoards() {
    let query = 'query { boards{ name id }}';

    fetch("https://api.monday.com/v2", {
      method: 'post',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjE3NjYwMTI4NCwidWlkIjozMzQ2MDkzNCwiaWFkIjoiMjAyMi0wOC0yMVQwMDoxMDo0Ni4wMDBaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6MTMxNTgyMzEsInJnbiI6InVzZTEifQ.LUmDgx0ng9YKgiqKtv0Em6p1mZ0mZNMFAOpOnvjwecQ'
      },
      body: JSON.stringify({
        'query': query
      })
    })
      .then(res => res.json())
      .then(res => {
        var mainBoard = res.data.boards.filter(function (el) {
          return el.name === "[ESG] Annual Goal Tracker & Overview"
        })
        setMainBoardID(mainBoard[0].id)
      });
  }

  function fetchMainBoard() {
    if (mainBoardID) {
      console.log("Fetching main board with mainBoardID...", mainBoardID)

      let query = `query {boards (ids:${mainBoardID}){items { column_values (ids:["numbers", "status", "text2", "text4"]) {
        id text 
      }}}} `;

      fetch("https://api.monday.com/v2", {
        method: 'post',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjE3NjYwMTI4NCwidWlkIjozMzQ2MDkzNCwiaWFkIjoiMjAyMi0wOC0yMVQwMDoxMDo0Ni4wMDBaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6MTMxNTgyMzEsInJnbiI6InVzZTEifQ.LUmDgx0ng9YKgiqKtv0Em6p1mZ0mZNMFAOpOnvjwecQ'
        },
        body: JSON.stringify({
          'query': query
        })
      })
        .then(res => res.json())
        .then(res => {
          setMainBoardItems(res.data.boards[0].items)
        });
    }
  }

  function calculateScore() {
    if (mainBoardItems) {
      randomScore()
      let tempScore = 10
      mainBoardItems.forEach(element => {
        let col = element.column_values
        col.forEach(val => {
          console.log(val)
          if (val.id === "status") {
            console.log(val)
          }
          if (val.id === "status" && val.text === 'Working on it') {
            // do something to score here

          }
          if (val.id === 'status' && val.text === 'Overused') {
            // do something to score here
          }
          if (val.id === 'status' && val.text === 'Underused') {
            // do something to score here
          }
          if (val.id === 'status' && val.text === 'Done') {
            // do something to score here
          }
        })
      });
    }
  }

  function convertScoreToRating() {
    if (score > 9.0) {
      setRating("AAA")
      setColor("#258750")
      return
    }
    if (score > 8.0) {
      setRating("AA")
      setColor("#9cd326")
      return
    }
    if (score > 7.0) {
      setRating("BBB")
      setColor("#ffcb00")
      return
    }
    if (score > 6.0) {
      setRating("BB")
      setColor("#fdab3d")
      return
    }
    if (score > 5.0) {
      setRating("CCC")
      setColor("#ff7575")
      return
    }
    if (score > 4.0) {
      setRating("CC")
      setColor("#e2445c")
      return
    }
    setRating("F")
    setColor("#bb3354")
  }

  function getRandomFloat(min, max, decimals) {
    const str = (Math.random() * (max - min) + min).toFixed(decimals);

    return parseFloat(str);
  }
  function randomScore() {
    let randScore = getRandomFloat(0.0, 10.0, 2);
    console.log(randScore)
    setScore(randScore)
  }


  useEffect(() => {
    fetchBoards()
  }, [])

  useEffect(() => {
    convertScoreToRating()
  }, [score])

  useEffect(() => {
    fetchMainBoard()
  }, [mainBoardID])


  useEffect(() => {
    calculateScore()
  }, [mainBoardItems])

  return (
    <div className="App">
      <h1 style={{ fontSize: '40vh', color: `${color}` }}>{rating}</h1>
    </div>
  )
}

export default App;
