import React, { useState, useEffect } from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css"
//Explore more Monday React Components here: https://style.monday.com/
import AttentionBox from "monday-ui-react-core/dist/AttentionBox.js"
import Button from "monday-ui-react-core/dist/Button.js"

const monday = mondaySdk();


const App = () => {

  const [score, setScore] = useState(10)
  const [rating, setRating] = useState("AAA")

  function convertScoreToRating() {
    if (score > 9.0) {
      setRating("AAA")
      return
    }
    if (score > 8.0) {
      setRating("AA")
      return
    }
    if (score > 7.0) {
      setRating("BBB")
      return
    }
    if (score > 6.0) {
      setRating("BB")
      return
    }
    if (score > 5.0) {
      setRating("CCC")
      return
    }
    if (score > 4.0) {
      setRating("CC")
      return
    }
    if (score > 3.0) {
      setRating("F")
    }
  }



  function getRandomFloat(min, max, decimals) {
    const str = (Math.random() * (max - min) + min).toFixed(decimals);

    return parseFloat(str);
  }

  function randomScore() {
    let randScore = getRandomFloat(0.0, 10.0, 2);
    console.log(randScore)
    setScore(randScore)
  }

  useEffect(() => {
    convertScoreToRating()
  }, [score])

  return (
    <div className="App">
      <h1 style={{ fontSize: "40vh" }}>{rating}</h1>
      <br />
      <Button onClick={randomScore}>Click me </Button>
    </div>
  )
}

export default App;
